import React, { useState, useEffect } from 'react';
import BookCard from './BookCard';
import { booksData } from '../data/booksData';

const FeaturedBooks = () => {
  // กำหนด State สำหรับจัดการข้อมูล
  const [featuredBooks, setFeaturedBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        setLoading(true);

        // ดึงข้อมูลจาก localStorage (หนังสือที่เพิ่มเอง)
        const customBooks = JSON.parse(localStorage.getItem('books') || '[]');

        // รวมข้อมูลทั้งหมด
        const allBooks = [...booksData, ...customBooks];

        // สุ่มหนังสือ 3 เล่ม หรือเลือกที่มี rating สูง
        const topRatedBooks = allBooks
          .filter(book => book.rating >= 4.5 || book.discount > 0)
          .sort((a, b) => (b.rating || 0) - (a.rating || 0))
          .slice(0, 3);

        setFeaturedBooks(topRatedBooks);
        setError(null);

      } catch (err) {
        setError(err.message);
        console.error('Error fetching books:', err);

      } finally {
        setLoading(false);
      }
    };

    // เรียกใช้ฟังก์ชันดึงข้อมูล
    fetchBooks();
  }, []); // [] = dependency array ว่าง = รันครั้งเดียว

  // กรณีกำลังโหลดข้อมูล
  if (loading) {
    return (
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
        <div className="text-center py-8 col-span-full">
          Loading...
        </div>
      </div>
    );
  }

  // กรณีเกิดข้อผิดพลาด
  if (error) {
    return (
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
        <div className="text-center py-8 col-span-full text-red-600">
          Error: {error}
        </div>
      </div>
    );
  }

  // กรณีแสดงผลข้อมูลปกติ
  return (
    <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
      {featuredBooks.map(book => (
        //แสดงผลผ่าน BookCard component
        <BookCard 
          key={book.id} 
          book={book} 
        />
      ))}
    </div>
  );
};

export default FeaturedBooks;
