import React, { useState, useEffect } from 'react';
import BookCard from './BookCard';
import { booksData } from '../data/booksData';

const NewBooks = () => {
  // กำหนด State สำหรับจัดการข้อมูล
  const [newBooks, setNewBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchNewBooks = async () => {
      try {
        setLoading(true);

        // ดึงข้อมูลจาก localStorage ก่อน (หนังสือที่เพิ่มเอง)
        const customBooks = JSON.parse(localStorage.getItem('books') || '[]');

        // รวมข้อมูลจาก booksData และ localStorage
        const allBooks = [...booksData, ...customBooks];

        // กรองหนังสือใหม่ (isNew = true) หรือเรียงตามวันที่สร้างล่าสุด
        const newBooksFiltered = allBooks
          .filter(book => book.isNew === true || customBooks.some(cb => cb.id === book.id))
          .slice(0, 6); // แสดง 6 เล่มแรก

        setNewBooks(newBooksFiltered);
        setError(null);

      } catch (err) {
        setError(err.message);
        console.error('Error fetching new books:', err);

      } finally {
        setLoading(false);
      }
    };

    // เรียกใช้ฟังก์ชันดึงข้อมูล
    fetchNewBooks();
  }, []); // [] = dependency array ว่าง = รันครั้งเดียว

  // กรณีกำลังโหลดข้อมูล
  if (loading) {
    return (
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
        <div className="text-center py-8 col-span-full">
          Loading new arrivals...
        </div>
      </div>
    );
  }

  // กรณีเกิดข้อผิดพลาด
  if (error) {
    return (
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
        <div className="text-center py-8 col-span-full text-red-600">
          Error: {error}
        </div>
      </div>
    );
  }

  // กรณีแสดงผลข้อมูลปกติ
  return (
    <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
      {newBooks.map(book => (
        <BookCard 
          key={book.id} 
          book={book} 
        />
      ))}
    </div>
  );
};

export default NewBooks;
